use Khaospy::Utils qw/burp/;

sub CONF_LIVE {"live"};
sub CONF_TEST {"test"};

our @EXPORT_OK = qw(
    CONF_LIVE
    CONF_TEST
    write_out_conf
);


my $live_confs = {

    $HEATING_DAEMON_CONF
        => live_heating_thermometer_config(),
    $RULES_DAEMON_CONF
        => live_rules_conf(),
    $CONTROLS_CONF
        => live_controls_conf(),
    $BOILERS_CONF
        => live_boilers_conf(),
    $GLOBAL_CONF
        => live_global_conf(),
    $PI_HOSTS_CONF
        => live_pi_host_conf(),

};

my $test_confs = {
    $HEATING_DAEMON_CONF
        => test_heating_thermometer_config(),
    $RULES_DAEMON_CONF
        => test_rules_conf(),
    $CONTROLS_CONF
        => test_controls_conf(),
    $BOILERS_CONF
        => test_boilers_conf(),
    $GLOBAL_CONF
        => test_global_conf(),
    $PI_HOSTS_CONF
        => test_pi_host_conf(),

};

sub write_out_conf {

    my ($live_or_test) = @_;

    my $use_conf;

    if ($live_or_test eq CONF_LIVE ) {
        $use_conf = $live_confs;
    } elsif ($live_or_test eq CONF_TEST ) {
        $use_conf = $test_confs;
    } else {
        die "didn't define live or test correctly to write out the hardcoded conf\n";
    }

    for my $conf_file ( keys %$use_conf ) {

        print "Generating $CONF_DIR/$conf_file\n";

        burp ( "$CONF_DIR/$conf_file",
                $JSON->pretty->encode( $use_conf->{$conf_file} )
        );
    }
}
